#!perl.exe -w
#To continue from the point you left on:
#tail -5 <output> (Last line will be incomplete, use the first one for verification).
#make $RawNum  = to answer
#Make $StartLoop = to Power

use vcfw;
$RawNum="";

$OutFile = "c:\\wv\\Pow2.txt";
open (OUT,">$OutFile") || die "choke on open $OutFile: $!";
#print OUT "2^0 = 1\n";
$T1 = time();
$StartLoop=118923;
for ($Loop=$StartLoop;$Loop<1000001;$Loop++) {
  $Carry = 0;
  $L = length($RawNum)-1;
  for($x=$L;$x >=0;$x--) {
    $Digit = substr($RawNum,$x,1);
    $NewDigit = $Digit + $Digit + $Carry;
    if ($NewDigit > 9) {
      $NewDigit = $NewDigit - 10;
      $Carry = 1;
    } else {
      $Carry = 0;
    }
    $NewSum = $NewDigit.$OldSum;
    $OldSum = $NewSum;
  }
  $NewSum = $Carry.$NewSum if ($Carry > 0);
  $OldSum = "";
  $RawNum = $NewSum;
  $Out = Commify($NewSum);
  $LoopOut = $Loop +1;
  print OUT "2^$LoopOut = $Out\n";
  if (($Loop/256) == (int($Loop/256))) {
    $T = time;
    $Elapse = $T - $T1;
    GenDTG();
    print "$DTG ($Elapse): $Loop : Lng=$L\n";
    $T1 = $T;
  }
}
close (OUT);
#system "read $OutFile";
system ("start c:\\progra~2\\notepad++\\notepad++.exe $OutFile")

